package lezione3Adecco;
/*
 * crea un programma 
 * che stampa i numeri al contrario 
 * 
 * 
 */

public class Esercizio8Cicli {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int index = 10;
		while(index>=1) {
			System.out.print(index+" - ");
			index --;
		}
	}
}


